			def highlight_text(text_widget, word, tag):
				start_index = "1.0"
				end_index = "end"
				text = text_widget.get(start_index, end_index)
				start = text_widget.search(word, start_index, end_index)
				while start:
					end = text_widget.index(f"{start}+{len(word)}c")
					text_widget.tag_add(tag, start, end)
					start = text_widget.search(word, f"{end}+1c", end_index)

			def highlight_text_between(text_widget, start_word, end_word, tag):
				start_index = "1.0"
				end_index = "end"
				text = text_widget.get(start_index, end_index)
				start = text_widget.search(start_word, start_index, end_index)
				while start:
					end = text_widget.search(end_word, start, end_index)
					if end:
						next_newline = text_widget.search("\n", start, end)
						if next_newline:
							text_widget.tag_add(tag, start, next_newline)
						else:
							text_widget.tag_add(tag, start, end)
					start = text_widget.search(start_word, f"{end}+1c", end_index)

