	# отримуємо значення останнього запису в таблицях presentation i meed
	# для того, щоб організувати автоматичне вставляння даних в форму
	# по натисканню Shift+Space

	def clipboard_pm(event=None, widget=None):
		if widget is None:
			return  # Выход из функции, если widget не был передан

		# подключаем базу
		db = sqlite3.connect(database_file_path)
		cursor = db.cursor()
		cursor.execute(f"PRAGMA key = '{password}';")

		# отримуємо значення останнього запису в таблиці presentation
		if widget == "PresNUMB" or widget == "PresREPORT":
			cursor.execute("SELECT registration, report FROM presentation ORDER BY rowid DESC LIMIT 1")
			clip_resentation = cursor.fetchone()
			if clip_resentation:
				if widget == "PresNUMB":
					PresNUMB.delete("0", END)
					PresNUMB.insert(END, str(clip_resentation[0]).strip())
				elif widget == "PresREPORT":
					PresREPORT.delete("0", END)
					PresREPORT.insert(END, str(clip_resentation[1]).strip())

		# отримуємо значення останнього запису в таблиці meed
		elif widget == "ConsingN" or widget == "LawDecreeN" or widget == "AwardDEC":
			cursor.execute("SELECT decree, consignment_note, id_award FROM meed ORDER BY rowid DESC LIMIT 1")
			clip_meed = cursor.fetchone()
			if clip_meed:
				if widget == "ConsingN":
					ConsingN.delete("0", END)
					ConsingN.insert(END, str(clip_meed[1]).strip())
				elif widget == "LawDecreeN":
					LawDecreeN.delete("0", END)
					LawDecreeN.insert(END, str(clip_meed[0]).strip())
				elif widget == "AwardDEC":
					all_items = AwardDEC['values']
					for item in cIIAwardlist:
						# Извлечение номера после '*'
						if str(item.split('*')[-1]) == str(clip_meed[2]):
							index = all_items.index(item)
							AwardDEC.current(index)       # Передаем числовой индекс в current()
							break

		# отключаем базу
		cursor.close()
		db.close()


