	# функція обробки дати для вставляння в поле DateEntry
	# вона форматує дати для обробки функцією DateEntry()
	# (прибирає години хвилини та секунди зі строки виводу)
	def DateDot2Hyphen(DateDot):
		# убираем пробели вначале и конце
		DateHyphen = DateDot.strip()
		# проверяем человеческую хуйню в датах
		if (DateHyphen 
			and DateHyphen.count(".") == 2 
			and len(DateHyphen) == 10
			and "-" not in DateHyphen 
			and DateHyphen.lower() not in ["", "none"]
		):
			try:
				# Replace dots with hyphens for consistent formatting
				DateHyphen = DateHyphen.replace('.', '-')
				# Fix unexpected spaces within the date string
				DateHyphen = DateHyphen.replace(' ', '-')
				# Parse the date string
				parsed_date = datetime.strptime(DateHyphen, '%d-%m-%Y')
				# Convert to 'YYYY-MM-DD' format
				DateHyphen = parsed_date.strftime('%Y-%m-%d')
			except ValueError:
				# Log or handle incorrect date formats
				print(f"Invalid date format: {DateDot}")
				raise
		return DateHyphen



