	# функції прив'язування / відв'язування нагороди до подання
	def save_linkORunlinkpresAaward(id_link):
		# id нагороди
		id_meed_link=id_link[1]
		# id подання
		id_presentation_link=id_link[0].split(" ")
		id_presentation_link=id_presentation_link[0]

		# переменная будет содержать некий текст
		text_infoM=""
		# подключаем базу
		db = sqlite3.connect(database_file_path)
		cursor = db.cursor()
		cursor.execute(f"PRAGMA key = '{password}';")
		# вносим данние в таблицу presentation о соответствующей ей награде
		# 1) определяем наличие привязки в базе
		# и если есть - отвязиваем, в ином случае - привязиваем
		cursor.execute("SELECT id_meed FROM presentation WHERE id = :id_presentation", \
			{"id_presentation": id_presentation_link, "id_meed": id_meed_link})
		result_link_prawa=cursor.fetchone()

		if result_link_prawa[0] == id_meed_link:
			# отвязиваем
			cursor.execute("UPDATE presentation SET id_meed = NULL \
				WHERE id = :id_presentation_link",
				{"id_presentation_link": id_presentation_link})
			text_infoM="Нагорода відв'язана від подання"
		else:
			# привязиваем
			cursor.execute("UPDATE presentation SET id_meed = :id_meed \
				WHERE id = :id_presentation",
				{"id_presentation": id_presentation_link, "id_meed": id_meed_link})
			text_infoM="Нагорода та подання пов'язані"
		# сохраняемся
		try:
			# робимо запис про останню зміну бази даних в таблицю service_
			last_updated = int(time.time())
			cursor.execute("UPDATE service_ SET last_time_changes = ? WHERE id = 1", (last_updated,))

			# повідомлення про успіх
			messagebox.showinfo("Успіх", text_infoM)

			# возвращаем исходний вид кнопке "Зберегти"
			buttonINStab2.config(relief="raised", bg="#d9d9d9")

			# зберігаємо
			db.commit()

		finally:
			# Всегда закрывайте соединение с базой данных в блоке finally
			cursor.close()
			db.close()
			getSearchEntry()


