	# якщо натиснутий чекбуттон, виводимо комбобокс - назви нагород
	def DecreeAward():

		# подключаем базу
		db = sqlite3.connect(database_file_path)
		cursor = db.cursor()
		cursor.execute(f"PRAGMA key = '{password}';")
		# вибираем название (уже награжденние) наград и сортируем по рангу
		cursor.execute("""
			SELECT date_decree, \
				(SELECT denotation FROM award \
					WHERE  award.id = meed.id_award) \
				AS denotation, \
				(SELECT id FROM award \
					WHERE  award.id = meed.id_award) \
				AS id, \
				(SELECT ranking FROM award \
					WHERE  award.id = meed.id_award) \
				AS ranking \
			FROM meed \
			WHERE date_decree is not Null \
			ORDER BY REPLACE(REPLACE(REPLACE(REPLACE(denotation, '"', ''), '«', ''), '»', ''), '-', '')
			""")
		
		awardsId2 = cursor.fetchall()
		# отключаем базу
		cursor.close()
		db.close()
		# собираем массив названий наград для випадающего списка
		comboAwardlist2=[]
		for row in awardsId2:
			xg=f'{row[3]}- {row[1]}                                    [{row[2]}]'
			comboAwardlist2.append(xg)
		# вибираємо унікальні записи для формирования комбосписка
		def select_unique_elements(input_list):
			unique_list = []
			for element in input_list:
				if element not in unique_list:
					unique_list.append(element)
			return unique_list
		unique_elements = select_unique_elements(comboAwardlist2)

		return unique_elements


