	# класс для створення окремих вікон з таблицями звітів
	class Table(tk.Frame):
		def __init__(self, parent=None, headings=tuple(), rows=tuple()):
			super().__init__(parent)

			def close_root_x(event=None):
				root_x.destroy()		

			# функция при нажатии на елемент списка виводит окошко с расширенним текстом
			# например - текстом ПОДАННЯ
			def on_select(event):
				global root_x
				# вывод текстовых id всех выбранных строк
				# (их может быть несколько, если при создании дерева не было указано selectmode='browse')
				# Если привязывались не к событию <<TreeviewSelect>>,
				# то тут нужно проверить, что вообще что-то выбрано:
				if not table.selection():
					return

				# Получаем id первого выделенного элемента
				selected_item = table.selection()[0]
				# Получаем значения в выделенной строке
				values = table.item(selected_item, option="values")
				id_values_find=len(values)-1
				# подключаем базу
				db = sqlite3.connect(database_file_path)
				cursor = db.cursor()
				cursor.execute(f"PRAGMA key = '{password}';")
				cursor.execute("SELECT id FROM personality WHERE name == :name", {"name": values[1]})
				res_val = cursor.fetchone()
				
				if res_val is not None:
					cursor.execute("SELECT text_presentation FROM presentation \
						WHERE id == :id", {"id": values[id_values_find]})
					text_pres = cursor.fetchone()
					textPpres = "дані відсутні"
					if text_pres:
						textPpres = text_pres[0]
					# виводимо окно з вибраними даними у вигляді таблиці
					root_x = tk.Toplevel(window)

					# Размещение окна по центру екрана
					root_x_width = 600  # Ширина окна
					root_x_height = 380  # Высота окна
					center_window(root_x, root_x_width, root_x_height)

					root_x.resizable (False,False) # заборона зміни розміру вікна
					root_x.title("Текст подання    [закрити - клік правою кнопкою]")
					ttk.Label(root_x, text=values[1], background='#d9d9d9',
						foreground="black").grid(row=0, column=0, sticky=tk.W, padx=10, pady=10)
					text_Presen = tk.Text(root_x, height="20", width="90",
									foreground='black',  wrap=WORD,
									background='snow',
									font = ("Sans",8))
					text_Presen.insert(tk.INSERT, textPpres)
					text_Presen.grid(row=1, column=0, sticky=tk.W, padx=10)
					text_Presen.bind('<Control-v>', lambda event: paste_from_clipboard(event, text_Presen))

					# Настроим полосу прокрутки
					scrollbar_text_Presen = tk.Scrollbar(root_x, bg="#d9d9d9")
					scrollbar_text_Presen.config(command=text_Presen.yview)
					scrollbar_text_Presen.grid(row=1, column=1, sticky=tk.NS)
					
					# сконфигурируем text_Presen
					text_Presen.configure(yscrollcommand=scrollbar_text_Presen.set, state="disabled")
					root_x.configure(bg='#d9d9d9')
					root_x.bind("<Button-3>", close_root_x)
					root_x.mainloop()

				# отключаем базу
				cursor.close()
				db.close()

			# рисуем табличку с вибраними данними
			#selectmode must be none, browse, or extended
			table = ttk.Treeview(self, show="headings", selectmode="browse")
			table["columns"]=headings
			table["displaycolumns"]=headings
			# настройка ширини столбцов по умолчанию
			column_widths = {"звання": 50, "підрозділ": 50, "ініціатор": 50, "указ/наказ": 50,
							'номер подання': 50, 'дата вручення нагороди': 70}
			for head in headings:
				table.heading(head, text=head, anchor=tk.CENTER)
				table.column(head, width=column_widths.get(head, 100), anchor=tk.NW)
			for row in rows:
				table.insert('', tk.END, values=tuple(row))

			scrolltable = tk.Scrollbar(self, command=table.yview, bg="#d9d9d9")
			table.config(yscrollcommand=scrolltable.set, cursor="hand2")
			scrolltable.pack(side=tk.RIGHT, fill=tk.Y)
			table.pack(expand=tk.YES, fill=tk.BOTH)
			table.bind('<<TreeviewSelect>>', on_select)

