	# функція ДРУКУЄ вибрані дані rankPresEs у вікно або в файл
	def show_presentation(modeSelData, rankPresEs, titlestr):

		# Подключение к базе данных
		db = sqlite3.connect(database_file_path)
		cursor = db.cursor()
		cursor.execute(f"PRAGMA key = '{password}';")

		# Вывод в Excel
		if modeSelData == 1:

			# Создание книги Excel
			wb = Workbook()
			ws = wb.active
			ws = wb.create_sheet(titlestr[:30], 0)
			# Установка формата бумаги на A4
			ws.page_setup.paperSize = ws.PAPERSIZE_A4
			# Установка ориентации страницы на альбомную
			ws.page_setup.orientation = ws.ORIENTATION_LANDSCAPE
			# Добавление заголовков в лист
			# первая строка в файлі
			ws.append([titlestr])
			# Определение названий столбцов
			exel_column_titles = column_titles.copy()

			indices = [i for i, value in enumerate(selected_colums[:14]) if value == "1"]

			if len(indices) < 5:
				# Установка ориентации страницы на книжную если колонок меньше 5
				ws.page_setup.orientation = ws.ORIENTATION_PORTRAIT

			pre_column_titles = [exel_column_titles[index] for index in indices]
			column_numb_row = "#"
			exel_column_titles = (column_numb_row,) + tuple(pre_column_titles)

			# Добавление заголовков столбцов с применением стилей
			header_row = ws.max_row  # Получаем номер последней строки
			for title in exel_column_titles:
				cell = ws.cell(row=header_row, column=exel_column_titles.index(title) + 1, value=title)
				cell.font = Font(name='Times New Roman', bold=True, size=9)
				cell.alignment = Alignment(horizontal='center', vertical='center', wrap_text=True)
				cell.border = Border(
					left=Side(border_style='thin'),
					right=Side(border_style='thin'),
					top=Side(border_style='thin'),
					bottom=Side(border_style='thin')
				)
				cell.fill = PatternFill(start_color="ccff99", end_color="ccff99", fill_type="solid")

			# Заполнение данными
			for rankPresE in rankPresEs:
				for row_index, row in enumerate(rankPresE):
					name_CND = ""
					denotatSndH = ""
					if (int(7) in indices) or (int(12) in indices):
						# если есть (int12) и 'хто отримав нагороду' , ищем его в базе
						cursor.execute("SELECT name FROM personality WHERE id == :handover", {"handover": row[13]})
						nameSndH = cursor.fetchone()
						if nameSndH:
							name_CND = nameSndH[0]
						else:
							name_CND = row[13]
						# если (int7) и есть нагорода виводим  ее название, если ="0" - виводим слово "відмовлено"
						if row[14] != "0":
							cursor.execute("SELECT denotation FROM award WHERE id == :id_award", {"id_award": row[11]})
							den_SndH = cursor.fetchone()
							denotatSndH = den_SndH[0] if den_SndH is not None else ''
							if row[17] == "1":
								denotatSndH += ' (посмертно)'
						else:
							denotatSndH = "відмовлено"

	
					# Добавляем ИНН / дату рождения к ПІБ
