	def chekPassChange():
		oldPass = pass1.get()
		newPass1 = pass2.get()
		newPass2 = pass3.get()

		if len(newPass1) == 0 or len(newPass2) == 0:
			messagebox.showinfo("Увага!", "Порожні паролі неприйнятні.")
			return
		if newPass1 != newPass2:
			messagebox.showinfo("Увага!", "Нові паролі не збігаються.")
			return

		try:
			# Подключаемся к базе с текущим паролем
			db = sqlite3.connect(database_file_path)
			cursor = db.cursor()
			cursor.execute(f"PRAGMA key = '{oldPass}';")

			# Проверяем, открылась ли база (например, пробуем простую команду)
			cursor.execute("SELECT name FROM sqlite_master WHERE type='table';")
			if cursor.fetchone() is None:
				messagebox.showwarning("Увага!", "Введено невірний старий пароль.")
				return

			# Меняем пароль базы
			cursor.execute(f"PRAGMA rekey = '{newPass2}';")
			db.commit()

			messagebox.showinfo("Успіх!", "Пароль бази даних успішно змінено.")
			on_closing_1()

		except sqlite3.Error as e:
			messagebox.showerror("Помилка бази даних", f"Помилка: {e}")
		finally:
			cursor.close()
			db.close()


