	def refresh_libs():
		db = sqlite3.connect(database_file_path)
		cursor = db.cursor()
		cursor.execute(f"PRAGMA key = '{password}';")

		# видаляємо строки де обидві ячейки порожні
		cursor.execute("SELECT id FROM libs WHERE unit_src is NULL AND rank_src is NULL")
		rows = cursor.fetchall()
		for row in rows:
			cursor.execute("DELETE FROM libs WHERE id=:id", {"id": row[0]})
			# зберігаємо дані
			db.commit()

		# ОНОВЛЮЄМО КОМБОБОКСИ У ВІДПОВІДНОСТІ ДО ЗМІН
		# удаляем старие данние
		UserUNIT_Comb2["values"] = []
		UserRank_Comb2["values"] = []
		UserUNIT_Comb["values"] = []
		UserRank_Comb["values"] = []
		UnitAvaliableArr=['']
		RankAvaliableArr=['']

		# получаем новие данние
		cursor.execute("SELECT unit_src, rank_src FROM libs")
		rows = cursor.fetchall()

		for row in rows:
			if row and row[0] is not None and row[0] != "":
				UnitAvaliableArr.append(row[0])
			if row and row[1] is not None and row[1] != "":
				RankAvaliableArr.append(row[1])

		# Обновляем значения Combobox
		UserUNIT_Comb2["values"] = UnitAvaliableArr
		UserRank_Comb2["values"] = RankAvaliableArr
		UserUNIT_Comb["values"] = UnitAvaliableArr
		UserRank_Comb["values"] = RankAvaliableArr

		# отключаем базу
		cursor.close()
		db.close()

		LibRankUnit_option_clear()


	# функція зміни/збереження глобальних змінних про Підрозділ та Звання
	def svLibs():
		def execute_db_query(query, params):
			db = sqlite3.connect(database_file_path)
			cursor = db.cursor()
			cursor.execute(f"PRAGMA key = '{password}';")
			cursor.execute(query, params)
			db.commit()
			cursor.close()
			db.close()

		def handle_deletion(old_value, column_name):
			if not messagebox.askokcancel("Увага! Небезпечна дія!!!", 
					f"Видалення {old_value} може зламати базу! Продовжити?"):
				LibRankUnit_option_clear()  # Вызываем при отмене
				return  # Прерываем выполнение функции
		    
			query = f"UPDATE libs SET {column_name} = NULL WHERE {column_name} = :value_to_delete"
			execute_db_query(query, {"value_to_delete": old_value})
			refresh_libs()

		def handle_update_or_insert(new_value, old_value, column_name):
			if len(new_value) >= 3:			
				if old_value != "":
					query = f"UPDATE libs SET {column_name} = ? WHERE {column_name} = ?"
					execute_db_query(query, (new_value, old_value))
				else:
					query = f"INSERT INTO libs ({column_name}) VALUES (?)"
					execute_db_query(query, (new_value,))
				refresh_libs()


		# Блок по званню
		new_value_rank = RankLibsEntry.get().strip()
		OLD_value_rank = UserRank_Comb2.get()

		# Блок по підрозділу
		new_value_unit = UnitLibsEntry.get().strip()
		OLD_value_unit = UserUNIT_Comb2.get()

		# ВИДАЛЕННЯ
		if (checkRankLibs_var.get() and OLD_value_rank != "") or (OLD_value_unit != "" and checkb_UnitLibs_var.get()):

			if OLD_value_unit != "":					
				handle_deletion(OLD_value_unit, "unit_src")

			if OLD_value_rank != "":					
				handle_deletion(OLD_value_rank, "rank_src")

		# ЗМІНА
		elif len(new_value_rank) > 3 or len(new_value_unit) >= 3:
			handle_update_or_insert(new_value_rank, OLD_value_rank, "rank_src")
			handle_update_or_insert(new_value_unit, OLD_value_unit, "unit_src")

		else:
			messagebox.askokcancel("Увага!", "Введіть осмислені дані")
			return


