	# чекбокс переключения поиска ОДИНОЧНИЙ/СПИСОК
	def on_trigger():
		global checked_trigger

		checked_trigger = not checked_trigger

		if checked_trigger:
			# чистим поле entry_last_name
			entry_last_name.delete("0", END)	
					
			# ховаємо entry_last_name
			entry_last_name.grid_remove()
			label_last_name.grid_remove()

			toggle_button_checkbox_.configure(text="<< 1 прізвище")
			# вибираємо подання без розгляду
			query_pr = """
				SELECT p.id AS id_personality, p.name
					FROM presentation pr
					JOIN personality p ON pr.id_personality = p.id
					WHERE pr.id_meed IS NULL AND pr.worker == 0
					ORDER BY \
					CASE \
						WHEN p.name GLOB '[А-Д]*' THEN 1 \
						WHEN p.name GLOB 'Є*' THEN 2 \
						WHEN p.name GLOB '[Е-З]*' THEN 3 \
						WHEN p.name GLOB 'І*' THEN 4 \
						WHEN p.name GLOB '[И-Я]*' THEN 5 \
						ELSE 6 \
					END, p.name
			"""
			try:
				# вибіраєм подання у залишку
				db = sqlite3.connect(database_file_path)
				cursor = db.cursor()
				cursor.execute(f"PRAGMA key = '{password}';")
				cursor.execute(query_pr)
				list_pres = cursor.fetchall()

				lbox1.grid(row=1, column=0, padx=5, pady=5, sticky=tk.W)
				scrollbar_lbox1.grid(row=1, column=1, padx=5, pady=5,  sticky="ns")
				f1.grid(row=1, column=2, padx=5, pady=5,  sticky=tk.W)
				button1.grid(row=1, column=2, padx=5, pady=5,  sticky=tk.W)
				button2.grid(row=2, column=2, padx=5, pady=5,  sticky=tk.W)
				lbox2.grid(row=1, column=3, padx=5, pady=5,  sticky=tk.W)
				scrollbar_lbox2.grid(row=1, column=4, padx=5, pady=5,  sticky="ns")

				# функция склоняет фамилии в винительном падеже
				def rodovyi_to_znachidnyy(prizvyshe):
					if prizvyshe.endswith(("ець")):
						return prizvyshe[:-3] + "ця"
					elif prizvyshe.endswith(("ий")):
						return prizvyshe[:-2] + "ого"
					elif prizvyshe.endswith(("ній")):
						return prizvyshe[:-2] + "ього"
					elif prizvyshe.endswith(("ня","ля","дя","мя","кя","ця","жя","бя","гя")):
						return prizvyshe[:-1] + "ю"
					elif prizvyshe.endswith(("ко","ло")):
						return prizvyshe[:-1] + "а"
					elif prizvyshe.endswith(("а")):
						return prizvyshe[:-1] + "у"
					elif prizvyshe.endswith(("нь","ль","ій","ай","дь")):
						return prizvyshe[:-1] + "я"
					else:
						return prizvyshe + "а"

				# обработка списка
				unique_list_pres = []
				for list_pres_i in list_pres:
					name_insert=list_pres_i[1].strip().split(" ")
					znachidnyy_prizvyshe = rodovyi_to_znachidnyy(name_insert[0].lower()).upper()
					# убираем повтори
					if znachidnyy_prizvyshe not in unique_list_pres:
						unique_list_pres.append(znachidnyy_prizvyshe)

				# вставка значений в listbox 1
				for r, list_pres_i in enumerate(unique_list_pres):
					lbox1.insert(END, list_pres_i)
					if r % 2 == 0:  # Если строка четная
						lbox1.itemconfig(r, bg="#ffffcc", fg="black")  # Устанавливаем фон и цвет текста

			except sqlite3.Error as e:
				messagebox.showerror("Увага",f"Помилка бази даних: {str(e)}")

			finally:
				# отключаем базу
				cursor.close()
				db.close()
		else:
			# чистим lbox1,2
			lbox1.delete(0, 'end')
			lbox2.delete(0, 'end')
			lbox1.grid_remove()
			lbox2.grid_remove()
			f1.grid_remove()
			button1.grid_remove()
			button2.grid_remove()
			scrollbar_lbox1.grid_remove()
			scrollbar_lbox2.grid_remove()
			toggle_button_checkbox_.configure(text=">> за списком")
			entry_last_name.grid(row=1, column=1, padx=5, pady=5, sticky="w")
			label_last_name.grid(row=1, column=0, padx=5, pady=5, sticky="w")


