	# --------- бокси для вибора десятилетия и года 

	def update_years():    
		sel_spinbox = year_spinbox.get()  # Получаем текущий выбранный год
		sel_decade = current_decade.get()  # Получаем текущее десятилетие
		current_year = datetime.now().year

		year_offset = 0
		if sel_spinbox:
			year_offset = int(sel_spinbox) % 10  # Остаток от деления определяет смещение внутри десятилетия    
		else:
			year_offset = current_year % 10 

		"""Обновление списка годов в зависимости от выбранного десятилетия."""
		# Формируем список годов десятилетия до текущего года
		years = [year for year in range(current_decade.get(), current_decade.get() + 10) if year <= current_year]

		# Устанавливаем доступные года в Spinbox для годов
		year_spinbox.config(values=tuple(years))     

		# Устанавливаем выбранный год
		if years:
			selected_year = years[min(year_offset, len(years) - 1)]  # Проверка, чтобы индекс не выходил за границы
		else:
			selected_year = current_year  # Если список годов пустой, используем текущий год

		year_spinbox.config(state="normal")
		year_spinbox.delete(0, "end")
		year_spinbox.insert(0, selected_year)
		year_spinbox.config(state="readonly")


	def increase_decade():
		"""Увеличение выбранного десятилетия на 10 лет."""
		# Проверяем, чтобы десятилетие не превышало текущего
		if current_decade.get() + 10 <= datetime.now().year:
			current_decade.set(current_decade.get() + 10)			
			button_up.config(text=f"{current_decade.get()}s:")
			button_down.config(text=f"{current_decade.get() - 10}s:")
			update_years()

	def decrease_decade():
		"""Уменьшение выбранного десятилетия на 10 лет."""
		current_decade.set(current_decade.get() - 10)
		button_down.config(text=f"{current_decade.get()}s:")
		button_up.config(text=f"{current_decade.get() + 10}s:")
		update_years()

