	# функция очистки result_websearch_text
	def remove_grid_group_8(progress, result_websearch_text):
		destroy_progress_bar(progress)
		on_trigger()
		button_stop.grid_forget()
		button_search_decree.grid(row=0, column=0, columnspan=2)
